import Categories from "../components/Categories";
import Sort from "../components/Sort";
import Pizza from "../components/Pizza";
import Skeleton from "../components/Skeleton";
import React from "react";


function Home () {
    const [items, setItems] = React.useState([]);
    const [loaded, setLoaded] = React.useState(false);
    const [categoryName, setCategoryName] = React.useState(0);
    const [sortName, setSortName] = React.useState({
        name : 'популярности',
        sortProperty : 'rating'
    });
    React.useEffect( () => {
        setLoaded(false);

        const order = sortName.sortProperty.includes('-') ? 'asc' : 'desc';
        const sortBy = sortName.sortProperty.replace('-', '');
        const category = categoryName > 0 ? `category=${categoryName}` : '';

        fetch(`https://631119f2826b98071a5208b4.mockapi.io/items?${category}&sortBy=${sortBy}&order=${order}`)
            .then((res) => res.json())
            .then((arr) => {
                setItems(arr);
                setLoaded(true);
            })
        window.scrollTo(0, 0);
    }, [categoryName, sortName]);
    return (
        <div className="container">
            <div className="content__top">
                <Categories value={categoryName} onChangeCategory={(i) => setCategoryName(i)}/>
                <Sort value={sortName} onChangeSort={(i) => setSortName(i)}/>
            </div>
            <h2 className="content__title">Все пиццы</h2>
            <div className="content__items">
                {
                    loaded ?
                        items.map( (pizza) =>
                            <Pizza
                                title={pizza.title}
                                price={pizza.price}
                                image={pizza.imageUrl}
                                types={pizza.types}
                                sizes={pizza.sizes}
                                key={pizza.id}
                            />
                        )
                        :
                        [...new Array(6)].map( (_, index) => (<Skeleton key={index} />))
                }
            </div>
            <ReactPaginate
                breakLabel="..."
                nextLabel="next >"
                onPageChange={handlePageClick}
                pageRangeDisplayed={8}
                pageCount={3}
                previousLabel="< previous"
                renderOnZeroPageCount={null}
            />
        </div>
    )
}

export default Home
